const initialState = {
	next_employee_id: 2,
	employees: [ { id: 1, signature: 'Robert Nowak' }, ]
}

const employeesReducer = (state = initialState, action) => {
	switch (action.type) {
		case 'EMPLOYEE_CREATE': {
			if ( action.value !== "" ) {
				const employee = {
					id: state.next_employee_id,
					signature: action.value,
					time_contract: 1,
					time_base: 496,
					time_left: 496,
					roles: []
				}

				let employees = state.employees.slice();
				employees.push( employee )

				let next_employee_id = state.next_employee_id + 1;
				return { ...state, employees, next_employee_id }
			} else {
				return { ...state }
			}
		}
		case 'EMPLOYEE_UPDATE': {
			let employees = state.employees.slice();
			employees.map(employee => {
				if( employee.id === action.id ) {
					employee[ action.filed ] = action.value;
				}
			})
			console.log(employees, action)
			return { ...state, employees }
		}
		default:
			return state;
	}
};

export default employeesReducer;
