const moment = require('moment')

const initialState = {
	settings: {
		is_absences_layer: false,
		is_time_layer: false,
	},
	employee_next_id: 1,
	employees: [],
	schedules: [],
	dates: [],
}

const navigationReducer = (state = { }, action) => {
	switch (action.type) {
		case 'SAVE': {
			const file = new Blob( [ JSON.stringify( state ) ], { type: 'application/json' } ) || null;

			const element = document.createElement("a");
			element.href = URL.createObjectURL( file );
			element.download = moment().format("YYYY-MM-DD HH:mm:ss") || "CarrotHR - ZAPIS";
			document.body.appendChild(element);
			element.click();

			return { ...state };
		}
		case 'LOADED': {
			let obj = action.payload;
			state.settings = obj.settings;
			state.employees = obj.employees;
			state.next_employee_id = obj.next_employee_id;
			state.schedules = obj.schedules;
			state.dates = obj.dates;
			return { ...state };
		}
		case 'GENERATE': {
			let schedules = state.schedules.slice()
			schedules.push({ id: 'XC2ad-aw@23a;2#;bTEr[21BvcD]', status: 'success' })
			return { ...state, schedules }
		}
		case 'CLEAR': {
			let schedules = state.schedules.slice()
			schedules.length = 0
			return { ...state, schedules }
		}
		case 'ABSENCES': {
			state.settings.is_absences_layer = ! state.settings.is_absences_layer
			state.settings.is_time_layer = false
			return { ...state }
		}
		case 'WORK_TIME': {
			state.settings.is_absences_layer = false
			state.settings.is_time_layer = ! state.settings.is_time_layer
			return { ...state }
		}
		case 'UNDO': {
			return { ...state }
		}
		case 'REDO': {
			return { ...state }
		}
		default:
			return state;
	}
};

export default navigationReducer;
