const initialState = {
	next_employee_id: 1,
	employees: [
		{ id: 1, signature: 'Adam Nowak' },
		{ id: 2, signature: 'Robert Nowak' },
	]
}

const employeesReducer = (state = initialState, action) => {
	switch (action.type) {
		case 'EMPLOYEE_CREATE': {
			let employees = state.employees.slice();
			employees.push( { id: state.next_employee_id, signature: action.value })
			let next_employee_id = state.next_employee_id + 1;
			return { ...state, employees, next_employee_id }
		}
		default:
			return state;
	}
};

export default employeesReducer;
