// settings = {}

const initialState = {
	is_absences_layer: false,
	is_time_layer: false,
}

const settingsReducer = ( state, action ) => {
	switch ( action.type ) {
		case 'ABSENCES': {
			state.is_absences_layer = ! state.is_absences_layer
			state.is_time_layer = false
			return { ...state }
		}
		case 'WORK_TIME': {
			state.settings.is_absences_layer = false
			state.settings.is_time_layer = ! state.settings.is_time_layer
			return { ...state }
		}
		default:
			return state
	}
}

export default settingsReducer;
