const initialState = {
	next_employee_id: 2,
	employees: [ { id: 1, signature: 'Robert Nowak' }, ]
}

const employeesReducer = (state = initialState, action) => {
	switch (action.type) {
		case 'EMPLOYEE_CREATE': {
			if ( action.value !== "" ) {
				const employee = {
					id: state.next_employee_id,
					signature: e.target.value,
					time_contract: 1,
					time_base: time_base,
					time_left: time_base,
					roles: []
				}

				let employees = state.employees.slice();
				employees.push( employee )

				let next_employee_id = state.next_employee_id + 1;
				return { ...state, employees, next_employee_id }
			} else {
				return { ...state }
			}
		}
		default:
			return state;
	}
};

export default employeesReducer;
