const initialState = {
	is_absences_layer: false,
	is_time_layer: false,
	max_daily_time: 12,
	daily_break: 13,
	weaky_break: 35,
	free_days: {
		sun: { shift: "", permanent: true},
		mon: null,
		tue: null,
		wed: null,
		thu: null,
		fri: null,
		sat: { shift: "", permanent: false},
	}
}

const settingsReducer = ( state = initialState, action ) => {
	switch ( action.type ) {
		case 'ABSENCES': {
			state.is_absences_layer = ! state.is_absences_layer
			state.is_time_layer = false
			return { ...state }
		}
		case 'WORK_TIME': {
			state.is_absences_layer = false
			state.is_time_layer = ! state.is_time_layer
			return { ...state }
		}
		default:
			return state
	}
}

export default settingsReducer;
