const moment = require('moment')

const reducer = (state, action) => {
	switch (action.type) {
		case 'SETTINGS':
			return { ...state };
		case 'SAVE':
			const element = document.createElement("a");
			const file = new Blob( [ JSON.stringify( state ) ], { type: 'application/json' } );
			element.href = URL.createObjectURL( file );
			element.download = moment().format("YYYY-mm-DD HH:MM:ss") || "CarrotHR - ZAPIS";
			document.body.appendChild(element);
			element.click();

			return { ...state };
		case 'LOAD':
			return { ...state };
		case 'ABSENCES':
			state.settings.is_absences_layer = ! state.settings.is_absences_layer
			state.settings.is_time_layer = false
			return { ...state }
		case 'WORK_TIME':
			state.settings.is_absences_layer = false
			state.settings.is_time_layer = ! state.settings.is_time_layer
			return { ...state }
		default:
			return state;
	}
};

export default reducer;
