const schedulesReducer = (state = [], action) => {
	switch (action.type) {
		case 'GENERATE': {
			state = state.slice()
			state.push({ id: 'XC2ad-aw@23a;2#;bTEr[21BvcD]', status: 'success' })
			console.log( state )
			return { ...state }
		}
		case 'CLEAR': {
			state = state.slice()
			state.length = 0
			return { ...state }
		}
		default:
			return state;
	}
};

export default schedulesReducer;
