import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Navbar, Button } from 'react-bootstrap';

const mapStateToProps = (state) => {
  	return {
		is_absences_layer: state.settings.is_absences_layer,
		is_time_layer: state.settings.is_time_layer,
	};
};
const mapDispatchToProps = (dispatch) => {
	return {
		onShowSettings: () => dispatch({ type: 'SHOW_SETTINGS'}),
		onSave: () => dispatch({ type: 'SAVE' }),
		onLoad: () => dispatch({ type: 'LOAD' }),
		onGenerate: () => dispatch({ type: 'GENERATE' }),
		onClear: () => dispatch({ type: 'CLEAR' }),
		onShowAbsences: () => dispatch({ type: 'SHOW_ABSENCES' }),
		onShowWorkTime: () => dispatch({ type: 'SHOW_WORK_TIME' }),
		onUndo: () => dispatch({ type: 'UNDO' }),
		onRedo: () => dispatch({ type: 'REDO' }),
	}
};

class Navigation extends Component {
	render() {
		const { is_absences_layer, is_time_layer, onShowSettings, onSave, onLoad, onGenerate, onClear, onShowAbsences, onShowWorkTime, onUndo, onRedo } = this.props;

		return ( <>
			<Navbar bg="light">
				<Button className="mr-4" variant="outline-warning" onClick={ onShowSettings } ><b> Ustawienia </b></Button>
				<Button className="mr-2" variant="outline-dark" onClick={ onSave } ><b> Zapisz </b></Button>
				<Button className="mr-4" variant="outline-dark" onClick={ onLoad } ><b> Wczytaj </b></Button>
				<Button className="mr-2" variant="outline-success" onClick={ onGenerate } ><b> Generuj </b></Button>
				<Button className="mr-4" variant="outline-danger" onClick={ onClear } ><b> Wyczyść </b></Button>
				<Button className="mr-2" variant="outline-info" onClick={ onShowAbsences } ><b>
					Pokaż absencje
				</b></Button>
				<Button className="mr-4" variant="outline-info" onClick={ onShowWorkTime } ><b>
					Pokaż czas pracy
				</b></Button>
				<Button className="mr-2" variant="outline-dark" onClick={ onUndo } ><b> Cofnij </b></Button>
				<Button className="mr-2" variant="outline-dark" onClick={ onRedo } ><b> Przywróć </b></Button>
			</Navbar>
		</> );
	}
}

export default Navigation = connect(mapStateToProps, mapDispatchToProps)(Navigation);
