import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Modal, Container, Row, Col, Button, Form, Table } from 'react-bootstrap';
import { getDayShortName } from '../helpers'

const moment = require('moment')

function Schedule() {
	const [modalShow, setModalShow] = useState(false);
	const dispatch = useDispatch();
	const { schedules } = useSelector( state => state.schedules );
	const dates = useSelector( state => state.dates );
	const { employees } = useSelector( state => state.employees );
	const edit_employee = {};

	return ( <>
		<Modal show={ modalShow } onHide={ () => setModalShow(false) } size="lg">
			<Modal.Header closeButton>
				<Modal.Title>
					Panel - { edit_employee.signature || null }
				</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<Container>
					<Row className="show-grid align-items-center mb-2">
						<Col sm={9} lg={10}> { edit_employee.signature || null } </Col>
						<Col sm={3} lg={2}> { edit_employee.id || null }	</Col>
					</Row>
				</Container>
			</Modal.Body>
			<Modal.Footer>
				<Button variant="danger" onClick={ () => setModalShow(false) }> Usuń </Button>
				<Button onClick={ () => setModalShow(false) }> Zamknij </Button>
			</Modal.Footer>
		</Modal>
		<Table className="carrotHR__table mb-0" striped bordered>
			<thead>
				<tr>
					<th>#</th>
					<th className="carrotHR__signature">Pracownik</th>
					{ dates.map(date => { return (
						<th className="text-nowrap text-center p-0">
							<span className="px-3">{ moment(date).format('MM-DD') }</span><hr className="m-0"/>
							<span className="px-3">{ getDayShortName( moment(date).format('ddd').toLowerCase() ) }</span>
						</th>
					)}) }
				</tr>
			</thead>
			<tbody>
				{ employees.map((employee, i) => { return (
					<tr>
						<th className="text-center">{ i + 1 }</th>
						<th className="text-nowrap" onClick={ () => { setModalData(employee); setModalShow(true); } }>{ employee.signature }</th>
						{ dates.map(date => { return (
							<td className="text-center align-middle p-0">
								<Form.Control className="carrotHR__input" type="text" size="sm"/>
								<Form.Control className="carrotHR__input" type="text" size="sm"/>
							</td>
						)}) }
					</tr>
				)})}
				<tr>
					<th className="text-center">#</th>
					<th className="text-nowrap">
						<Form.Control type="text" size="sm" onKeyDown={ (e) => { ( e.key === 'Enter' ) ? (() => { dispatch({ type: 'EMPLOYEE_CREATE', value: e.target.value }); e.target.value = ""; })() : (() => {})() } }/>
					</th>
					{ dates.map(date => { return (
						<td className="text-center"> </td>
					)}) }
				</tr>
			</tbody>
		</Table>
	</> );
}

export default Schedule;
