const initialState = { }
let next_employee_id = 1;

const employeesReducer = (state = initialState, action) => {
	switch (action.type) {
		case 'EMPLOYEE_CREATE': {
			if ( action.value !== "" ) {
				return {
					...state,
					[ next_employee_id ]: {
						signature: action.value,
						time_contract: 1,
						role: 'PRACOWNIK'
					}
				}
			} else {
				return { ...state }
			}
		}
		case 'EMPLOYEE_UPDATE': {
			console.log(action)
			return {
				...state,
				employees: {
					...state.employees,
					[ action.id ] : {
						signature: action.value
					}
				}
			}
		}
		default:
			return state;
	}
};

export default employeesReducer;
