const schedulesReducer = (state = [], action) => {
	switch (action.type) {
		case 'GENERATE': {
			let schedules = state.slice()
			schedules.push({ id: 'XC2ad-aw@23a;2#;bTEr[21BvcD]', status: 'success' })
			return { ...state, schedules }
		}
		case 'CLEAR': {
			let schedules = state.schedules.slice()
			schedules.length = 0
			return { ...state, schedules }
		}
		default:
			return state;
	}
};

export default schedulesReducer;
