const defaultReducer = ( root, action) => {
	switch( action.type ) {
		case 'SAVE': {
			console.log(root)
			return { ...root }
		}
		case 'LOAD': {
			console.log(root)
			return { ...root }
		}
		default:
			return root
	}
}

