import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Modal, Container, Row, Col, Button, Form, Table } from 'react-bootstrap';
import { getDayShortName } from '../helpers'

const moment = require('moment')

const EmployeeModal = (props) => {
	<Modal show={ modalShow } onHide={ () => setModalShow(false) } size="lg">
		<Modal.Header closeButton>
			<Modal.Title>
				Panel - { props.employee?.signature }
			</Modal.Title>
		</Modal.Header>
		<Modal.Body>
			<Container>
				<Row className="show-grid align-items-center mb-2">
					<Col sm={6} lg={8}> Dane personalne: </Col>
					<Col sm={6} lg={4}>
						<Form.Control type="text" value={ employees[ modalEmployeeKey ]?.signature } updateOn="blur" onChange={ (e) => dispatch({ type: 'EMPLOYEE_UPDATE', id: modalEmployeeKey, field: 'signature', value: e.target.value })}/>
					</Col>
				</Row>
				<Row className="show-grid align-items-center mb-2">
					<Col sm={6} lg={8}> Wymiar czasu pracy: </Col>
					<Col sm={6} lg={4}>
						<Form.Control as="select" value={ employees[ modalEmployeeKey ]?.time_contract } onChange={ (e) => dispatch({ type: 'EMPLOYEE_UPDATE', id: modalEmployeeKey, field: 'time_contract', value: e.target.value })} custom>
							<option value={1}>Pełny etat</option>
							<option value={0.75}>3/4 etatu</option>
							<option value={0.5}>Pół etatu</option>
							<option value={0.25}>Ćwierć etatu</option>
						</Form.Control>
					</Col>
				</Row>
				<Row className="show-grid align-items-center mb-2">
					<Col sm={6} lg={8}> Stanowisko: </Col>
					<Col sm={6} lg={4}>
						<Form.Control type="text" value={ employees[ modalEmployeeKey ]?.role } onChange={ (e) => dispatch({ type: 'EMPLOYEE_UPDATE', id: modalEmployeeKey, field: 'role', value: e.target.value.toUpperCase() })}/>
					</Col>
				</Row>
			</Container>
		</Modal.Body>
		<Modal.Footer>
			<Button variant="danger" onClick={ () => setModalShow(false) }> Usuń </Button>
			<Button onClick={ () => setModalShow(false) }> Zamknij </Button>
		</Modal.Footer>
	</Modal>
}

const ScheduleCell = React.memo((props) => {
	return (
		<td className="text-center align-middle p-0">
			<Form.Control className="carrotHR__input" type="text" size="sm"/>
			<Form.Control className="carrotHR__input" type="text" size="sm"/>
		</td>
	)
});

const ScheduleRow = React.memo((props) => {
	const dates = useSelector( state => state.dates );

	return (
		<tr>
			<th className="text-center">{ props.no }</th>
			<th className="text-nowrap">{ props.employee.signature }</th>
			{ dates.map(date => { return (
				<ScheduleCell />
			)}) }
		</tr>
	)
})

function Schedule() {
	const [modalShow, setModalShow] = useState(false);
	const [modalEmployeeKey, setModalEmployeeKey] = useState(0)
	const dispatch = useDispatch();
	const { schedules } = useSelector( state => state.schedules );
	const dates = useSelector( state => state.dates );
	const employees = useSelector( state => state.employees );

	return ( <>
		<EmployeeModal employee={ employees[ modalEmployeeKey ] }/>
		<Table className="carrotHR__table mb-0" striped bordered>
			<thead>
				<tr>
					<th>#</th>
					<th className="carrotHR__signature">Pracownik</th>
					{ dates.map(date => { return (
						<th className="text-nowrap text-center p-0">
							<span className="px-3">{ moment(date).format('MM-DD') }</span><hr className="m-0"/>
							<span className="px-3">{ getDayShortName( moment(date).format('ddd').toLowerCase() ) }</span>
						</th>
					)}) }
				</tr>
			</thead>
			<tbody>
				{ Object.entries(employees).map(([key, employee], i) => { return (
					<ScheduleRow no={ i + 1 } employee={ employee } key={ key }/>
				)})}
				<tr>
					<th className="text-center">#</th>
					<th className="text-nowrap">
						<Form.Control type="text" size="sm" placeholder="DODAJ PRACOWNIKA" onKeyDown={ (e) => { ( e.key === 'Enter' ) ? (() => { dispatch({ type: 'EMPLOYEE_CREATE', value: e.target.value }); e.target.value = ""; })() : (() => {})() } }/>
					</th>
					{ dates.map(date => { return (
						<td className="text-center"> </td>
					)}) }
				</tr>
			</tbody>
		</Table>
	</> );
}

export default Schedule;
