import React, { Component } from 'react';
import { connect, useDispatch } from 'react-redux';
import { Navbar, Button } from 'react-bootstrap';
// import { loaded } from '../reducers/navigation'

const mapStateToProps = (state) => {
  	return {
		schedules: state.schedules,
		is_absences_layer: state.settings.is_absences_layer,
		is_time_layer: state.settings.is_time_layer,
	};
};
const mapDispatchToProps = (dispatch) => {
	return {
		onSettings: () => dispatch({ type: 'SETTINGS'}),
		onSave: () => dispatch({ type: 'SAVE' }),
		onLoaded: (e) => dispatch({ type: 'LOADED', async: loaded(e.target.files[0]) }),
		onGenerate: () => dispatch({ type: 'GENERATE' }),
		onClear: () => dispatch({ type: 'CLEAR' }),
		onAbsences: () => dispatch({ type: 'ABSENCES' }),
		onWorkTime: () => dispatch({ type: 'WORK_TIME' }),
		onUndo: () => dispatch({ type: 'UNDO' }),
		onRedo: () => dispatch({ type: 'REDO' }),
	}
};

const loaded = ( file ) => ( dispatch ) => {
	// var reader = new FileReader();
	// reader.onload = ((reader) => {
	// 	return () => {
	// 		const json = JSON.parse(reader.result);
	// 		// dispatch(incrementByAmount(amount));
	// 		console.log("SIPCIA")
	// 	}
	// })(reader);
	// reader.readAsText(file);
	console.log("SIPCIA", file)
};

const ref = React.createRef();

function LoadButton() {
	const dispatch = useDispatch();

	return ( <>
		<Button className="mr-4" variant="outline-dark" type="input" onClick={ () => { ref.current.click() } } ><b> Wczytaj </b></Button>
		<input ref={ ref } className="d-none" type="file" onChange={ (e) => dispatch( loaded( e.target.files[0] ) ) }/>
	</> )
}

class Navigation extends Component {
	render() {
		const { schedules, is_absences_layer, is_time_layer, onSettings, onSave, onLoaded, onGenerate, onClear, onAbsences, onWorkTime, onUndo, onRedo } = this.props;

		return ( <>
			<Navbar bg="light">
				<Button className="mr-4" variant="outline-warning" onClick={ onSettings } ><b> Ustawienia </b></Button>
				<Button className="mr-2" variant="outline-dark" onClick={ onSave } ><b> Zapisz </b></Button>
				<LoadButton />
				<Button className="mr-2" variant="outline-success" onClick={ onGenerate } ><b> Generuj </b></Button>
				<Button className="mr-4" variant="outline-danger" onClick={ onClear } ><b> Wyczyść </b></Button>
				<Button className="mr-2" variant="outline-info" onClick={ onAbsences } ><b>
					{ ( is_absences_layer === false ) ? "Pokaż absencje" : "Ukryj absencje" }
				</b></Button>
				<Button className="mr-4" variant="outline-info" onClick={ onWorkTime } ><b>
					{ ( is_time_layer === false ) ? "Pokaż czas pracy" : "Ukryj czas pracy" }
				</b></Button>
				<Button className="mr-2" variant="outline-dark" onClick={ onUndo } ><b> Cofnij </b></Button>
				<Button className="mr-2" variant="outline-dark" onClick={ onRedo } ><b> Przywróć </b></Button>
			</Navbar>
			<Button>{ schedules.length }</Button>

		</> );
	}
}

export default Navigation = connect(mapStateToProps, mapDispatchToProps)(Navigation);
